# typed: false
module SorbetRails
  module CustomFinderMethods
    def find_n(*ids)
      find(ids)
    end

    def first_n(n)
      first(n)
    end

    def last_n(n)
      last(n)
    end

    # Redeclare these dynamic methods here otherwise they will be generated by
    # `srb rbi hidden-definitions`
    def find_by_id(id)
      find_by(id: id)
    end

    def find_by_id!(id)
      find_by!(id: id)
    end
  end
end
